changeSet.sql:
COMMENT ON COLUMN promo_codes.current_usage_count IS
'Current number of times this promo code has been used.
IMPORTANT: This counter is now persistent and incremented transactionally.';

-- Проверяем, что нет промо-кодов с невалидными датами
DO $$
BEGIN
    IF EXISTS (
        SELECT 1 FROM promo_codes
        WHERE valid_from > valid_to
    ) THEN
        RAISE EXCEPTION 'Data integrity error: Found promo codes with valid_from > valid_to';
    END IF;
END $$;

-- Проверяем, что нет промо-кодов с отрицательными значениями скидки
DO $$
BEGIN
    IF EXISTS (
        SELECT 1 FROM promo_codes
        WHERE discount_value < 0
    ) THEN
        RAISE EXCEPTION 'Data integrity error: Found promo codes with negative discount_value';
    END IF;
END $$;
